*/ namespace Module\Article\Admin\Controller; use Illuminate\Routing\Controller; use ModStart\Admin\Concern\HasAdminQuickCRUD; use ModStart\Admin\Layout\AdminCRUDBuilder; use ModStart\Field\AbstractField; use ModStart\Field\Type\FieldRenderMode; use ModStart\Form\Form; use ModStart\Grid\GridFilter; use ModStart\Support\Concern\HasFields; use ModStart\Widget\TextLink; use Module\Article\Type\ArticlePosition; use Module\Article\Util\ArticleUtil; class ArticleController extends Controller { use HasAdminQuickCRUD; protected function crud(AdminCRUDBuilder $U2qv4) { $U2qv4->init('article')->field(function ($U2qv4) { $U2qv4->id('id', 'ID'); $U2qv4->select('position', '位置')->optionType(ArticlePosition::class); $U2qv4->text('title', '名称')->hookRendering(function (AbstractField $E9h4W, $AVLNU, $oPu4k) { switch ($E9h4W->renderMode()) { case FieldRenderMode::GRID: case FieldRenderMode::DETAIL: return TextLink::primary(htmlspecialchars($AVLNU->title), ArticleUtil::url($AVLNU->toArray()), 'target="_blank"'); } }); $U2qv4->text('alias', '别名')->help('默认留空，可通过链接 /article/别名 访问，不能为纯数字'); $U2qv4->richHtml('content', '内容')->listable(false); $U2qv4->display('created_at', L('Created At'))->listable(false); $U2qv4->display('updated_at', L('Updated At'))->listable(false); })->gridFilter(function (GridFilter $RryBq) { $RryBq->eq('position', '位置')->select(ArticlePosition::class); $RryBq->like('title', L('Title')); })->title('通用文章')->hookSaved(function (Form $yAhCR) { ArticleUtil::clearCache(); }); } }