*/ namespace Module\Banner\Admin\Controller; use Illuminate\Routing\Controller; use ModStart\Admin\Concern\HasAdminQuickCRUD; use ModStart\Admin\Layout\AdminCRUDBuilder; use ModStart\Form\Form; use ModStart\Grid\GridFilter; use ModStart\Support\Concern\HasFields; use Module\Banner\Type\BannerPosition; use Module\Banner\Type\BannerPositionRemark; use Module\Banner\Type\BannerType; use Module\Banner\Util\BannerUtil; class BannerController extends Controller { use HasAdminQuickCRUD; protected function crud(AdminCRUDBuilder $U2qv4) { $U2qv4->init('banner')->field(function ($U2qv4) { $U2qv4->id('id', 'ID'); $U2qv4->select('position', '位置')->optionType(BannerPositionRemark::class); $U2qv4->image('image', '图片'); $U2qv4->radio('type', '样式类型')->optionType(BannerType::class)->when(BannerType::IMAGE_TITLE_SLOGAN_LINK, function ($rYP3a) { $rYP3a->text('title', '标题'); $rYP3a->textarea('slogan', '内容描述'); $rYP3a->text('linkText', '链接文字'); $rYP3a->switch('colorReverse', '颜色反转'); })->when(BannerType::VIDEO, function ($rYP3a) { $rYP3a->video('video', '视频'); })->required(); $U2qv4->color('backgroundColor', '背景色'); $U2qv4->link('link', '链接'); $U2qv4->display('created_at', L('Created At'))->listable(false); $U2qv4->display('updated_at', L('Updated At'))->listable(false); })->gridFilter(function (GridFilter $RryBq) { $RryBq->eq('position', '位置')->select(BannerPosition::class); })->hookChanged(function (Form $yAhCR) { BannerUtil::clearCache(); })->canBatchDelete(true)->enablePagination(false)->defaultOrder(array('sort', 'asc'))->canSort(true)->title('轮播图片'); } }