<?php
 namespace Workerman\Protocols; use Workerman\Connection\ConnectionInterface; class Text { public static function input($buffer, ConnectionInterface $connection) { if (isset($connection->maxPackageSize) && \strlen($buffer) >= $connection->maxPackageSize) { $connection->close(); return 0; } $pos = \strpos($buffer, "\n"); if ($pos === false) { return 0; } return $pos + 1; } public static function encode($buffer) { return $buffer . "\n"; } public static function decode($buffer) { return \rtrim($buffer, "\r\n"); } }