*/ namespace Module\Member\Admin\Controller; use Illuminate\Routing\Controller; use ModStart\Admin\Concern\HasAdminQuickCRUD; use ModStart\Admin\Layout\AdminCRUDBuilder; use ModStart\Form\Form; use ModStart\Grid\GridFilter; use ModStart\Support\Concern\HasFields; use Module\Member\Util\MemberGroupUtil; class MemberGroupController extends Controller { use HasAdminQuickCRUD; protected function crud(AdminCRUDBuilder $U2qv4) { $U2qv4->init('member_group')->field(function ($U2qv4) { $U2qv4->id('id', 'ID')->editable(true)->addable(true); $U2qv4->text('title', '名称'); $U2qv4->text('description', '描述'); $U2qv4->switch('isDefault', '默认')->optionsYesNo()->help(''); $U2qv4->switch('showFront', '前台显示')->optionsYesNo()->help(''); $U2qv4->display('created_at', L('Created At'))->listable(false); $U2qv4->display('updated_at', L('Updated At'))->listable(false); })->gridFilter(function (GridFilter $RryBq) { $RryBq->like('title', '名称'); })->enablePagination(false)->defaultOrder(array('id', 'asc'))->canSort(true)->title('用户分组')->dialogSizeSmall()->hookSaved(function (Form $yAhCR) { MemberGroupUtil::clearCache(); }); $U2qv4->repository()->setSortColumn('id'); } }